// Helper function to clean up function descriptions for better formatting
export function cleanFunctionDescription(description: string): string {
  if (!description) return '';
  
  let cleaned = description
    // Remove leading newlines and comment markers
    .replace(/^\n+/, '')
    // Remove leading number patterns like "// 221:" 
    .replace(/^\/\/\s*\d+:\s*/, '')
    // Remove all remaining comment markers
    .replace(/\/\/\s*/g, '')
    // Remove asterisk bullet points
    .replace(/\*\s+/g, '')
    // Remove multiple newlines and replace with single space
    .replace(/\n+/g, ' ')
    // Replace multiple spaces with single space
    .replace(/\s+/g, ' ')
    // Remove leading/trailing whitespace
    .trim();

  // Remove function signature patterns that got mixed in (more specific patterns)
  cleaned = cleaned
    .replace(/\b(void|int|float|string|object|vector|location|event|effect|itemproperty|talent|action)\s+[a-zA-Z_][a-zA-Z0-9_]*\s*\([^)]*\)\s*;?\s*$/g, '')
    .replace(/\b(void|int|float|string|object|vector|location|event|effect|itemproperty|talent|action)\s+[a-zA-Z_][a-zA-Z0-9_]*\s*\([^)]*\)\s*;?\s*$/, '')
    // Remove trailing periods and semicolons
    .replace(/[.;]+\s*$/, '')
    // Remove bullet point patterns like "- parameter:"
    .replace(/\s*-\s*\w+:\s*/g, '. ')
    // Clean up extra spaces created by replacements
    .replace(/\s+/g, ' ')
    .trim();

  // If the cleaned description is too short or empty, return a more meaningful default
  if (cleaned.length < 10) {
    return '';
  }

  return cleaned;
}

