  { name: "CreatureFlourishWeapon", returnType: "void", parameters: [{ name: "oObject", type: "object" }], description: "\n// 847 \n// AWD-OEI 7/22/2004 \n// This causes a creature to flourish with it's currently equipped weapon...", category: "Combat" },
  { name: "EffectMindTrick", returnType: "effect", parameters: [], description: "\n// 848 \n// Create a Mind Trick effect...", category: "Effect" },
  { name: "EffectFactionModifier", returnType: "effect", parameters: [{ name: "nValue", type: "int" }], description: "\n// 849 \n// Creates a Faction effect...", category: "Effect" },
  { name: "ChangeObjectAppearance", returnType: "void", parameters: [{ name: "oObjectToChange", type: "object" }, { name: "nAppearance", type: "int" }], description: "\n// 850 \n// ChangeObjectAppearance \n// oObjectToChange = Object to change appearance of \n// nAppearance = appearance to change to (from appearance.2da) void ChangeObjectAppearance( object oObjectToChange, int nAppearance );", category: "General" },
  { name: "GetIsXBox", returnType: "int", parameters: [], description: "\n// 851 \n// GetIsXBox \n// Returns TRUE if this script is being executed on the X-Box. Returns FALSE \n// if this is the PC build. int GetIsXBox();", category: "General" },
  { name: "EffectDroidScramble", returnType: "effect", parameters: [], description: "\n// 852 \n// Create a Droid Scramble effect effect EffectDroidScramble();", category: "Effect" },
  { name: "ActionSwitchWeapons", returnType: "void", parameters: [], description: "\n// 853 \n// ActionSwitchWeapons \n// Forces the creature to switch between Config 1 and Config 2 \n// of their equipment. Does not work in dialogs. Works with \n// AssignCommand() void ActionSwitchWeapons();", category: "Action" },
  { name: "PlayOverlayAnimation", returnType: "void", parameters: [{ name: "oTarget", type: "object" }, { name: "nAnimation", type: "int" }], description: "\n// 854 \n// PlayOverlayAnimation \n// This function will play an overlay animation on a character \n// even if the character is moving. This does not cause an action \n// to be placed on the queue. The animation passed in must be \n// designated as an overlay in Animations.2DA. void PlayOverlayAnimation( object oTarget, int nAnimation );", category: "Audio/Visual" },
  { name: "UnlockAllSongs", returnType: "void", parameters: [], description: "\n// 855 \n// RWT-OEI 08/30/04 \n// UnlockAllSongs \n// Calling this will set all songs as having been unlocked. \n// It is INTENDED to be used in the end-game scripts to unlock \n// any end-game songs as well as the KotOR1 sound track. void UnlockAllSongs();", category: "General" },
  { name: "DisableMap", returnType: "void", parameters: [{ name: "nFlag", type: "int", defaultValue: "FALSE" }], description: "\n// 856 \n// RWT-OEI 08/31/04 \n// Passing TRUE into this function turns off the player's maps. \n// Passing FALSE into this function re-enables them. This change \n// is permanent once called, so it is important that there *is* \n// a matching call to DisableMap(FALSE) somewhere or else the \n// player is stuck without a map indefinitely. void DisableMap(int nFlag = FALSE);", category: "General" },
  { name: "DetonateMine", returnType: "void", parameters: [{ name: "oMine", type: "object" }], description: "\n// 857 \n// RWT-OEI 08/31/04 \n// This function schedules a mine to play its DETONATION \n// animation once it is destroyed. Note that this detonates \n// the mine immediately but has nothing to do with causing \n// the mine to do any damage to anything around it. To \n// get the mine to damage things around it when it detonates \n// do: \n// AssignCommand(<mine>,ExecuteScript( \"k_trp_generic\",<mine>)); \n// right before you call DetonateMine(). By my experience so far \n// you don't need any kind of delay between the two. void DetonateMine(object oMine);", category: "General" },
  { name: "DisableHealthRegen", returnType: "void", parameters: [{ name: "nFlag", type: "int", defaultValue: "FALSE" }], description: "\n// 858 \n// RWT-OEI 09/06/04 \n// This function turns off the innate health regeneration that all party \n// members have. The health regen will *stay* off until it is turned back \n// on by passing FALSE to this function. void DisableHealthRegen(int nFlag = FALSE);", category: "General" },
  { name: "SetCurrentForm", returnType: "void", parameters: [{ name: "oCreature", type: "object" }, { name: "nFormID", type: "int" }], description: "\n// 859 \n// This function sets the current Jedi Form on the given creature. This \n// call will do nothing if the target does not know the Form itself. void SetCurrentForm(object oCreature, int nFormID);", category: "General" },
  { name: "SetDisableTransit", returnType: "void", parameters: [{ name: "nFlag", type: "int", defaultValue: "FALSE" }], description: "\n// 860 \n// RWT-OEI 09/09/04 \n// This will disable or enable area transit void SetDisableTransit(int nFlag = FALSE);", category: "General" },
  { name: "SetInputClass", returnType: "void", parameters: [{ name: "nClass", type: "int" }], description: "\n// 861 \n//RWT-OEI 09/09/04 \n// This will set the specific input class. \n// The valid options are: \n// 0 - Normal PC control \n// 1 - Mini game control \n// 2 - GUI control \n// 3 - Dialog Control \n// 4 - Freelook control void SetInputClass(int nClass);", category: "Character" },
  { name: "SetForceAlwaysUpdate", returnType: "void", parameters: [{ name: "oObject", type: "object" }, { name: "nFlag", type: "int" }], description: "\n// 862 \n// RWT-OEI 09/15/04 \n// This script allows an object to receive updates even if it is outside \n// the normal range limit of 250.0f meters away from the player. This should \n// ONLY be used for cutscenes that involve objects that are more than 250 \n// meters away from the player. It needs to be used on a object by object \n// basis. \n// This flag should *always* be set to false once the cutscene it is needed \n// for is over, or else the game will spend CPU time updating the object \n// when it doesn't need to. void SetForceAlwaysUpdate(object oObject, int nFlag);", category: "General" },
  { name: "EnableRain", returnType: "void", parameters: [{ name: "nFlag", type: "int" }], description: "\n// 863 \n// RWT-OEI 09/15/04 \n// This function enables or disables rain void EnableRain(int nFlag);", category: "General" },
  { name: "DisplayMessageBox", returnType: "void", parameters: [{ name: "nStrRef", type: "int" }, { name: "sIcon", type: "string" }], description: "\n// 864 \n// RWT-OEI 09/27/04 \n// This function displays the generic Message Box with the strref \n// message in it \n// sIcon is the resref for an icon you would like to display. void DisplayMessageBox(int nStrRef, string sIcon = \"\");", category: "Audio/Visual" },
  { name: "DisplayDatapad", returnType: "void", parameters: [{ name: "oDatapad", type: "object" }], description: "\n// 865 This function displays a datapad popup. Just pass it the \n// object ID of a datapad. void DisplayDatapad(object oDatapad);", category: "Audio/Visual" },
  { name: "RemoveHeartbeat", returnType: "void", parameters: [{ name: "oPlaceable", type: "object" }], description: "\n// 866 \n// CTJ-OEI 09-29-04 \n// Removes the heartbeat script on the placeable. Useful for \n// placeables whose contents get populated in the heartbeat \n// script and then the heartbeat no longer needs to be called. void RemoveHeartbeat(object oPlaceable);", category: "Movement" },
  { name: "RemoveEffectByID", returnType: "void", parameters: [{ name: "oCreature", type: "object" }, { name: "nEffectID", type: "int" }], description: "\n//867 \n// JF-OEI 10-07-2004 \n// Remove an effect by ID void RemoveEffectByID( object oCreature, int nEffectID );", category: "Effect" },
  { name: "RemoveEffectByExactMatch", returnType: "void", parameters: [{ name: "oCreature", type: "object" }, { name: "eEffect", type: "effect" }], description: "\n//868 \n// RWT-OEI 10/07/04 \n// This script removes an effect by an identical match \n// based on: \n// Must have matching EffectID types. \n// Must have the same value in Integer(0) \n// Must have the same value in Integer(1) void RemoveEffectByExactMatch( object oCreature, effect eEffect);", category: "Effect" },
  { name: "AdjustCreatureSkills", returnType: "void", parameters: [{ name: "oObject", type: "object" }, { name: "nSkill", type: "int" }, { name: "nAmount", type: "int" }], description: "\n// 869 \n// This function adjusts a creature's skills. \n// oObject is the creature that will have its skill adjusted void AdjustCreatureSkills(object oObject, int nSkill, int nAmount);", category: "General" },
  { name: "GetSkillRankBase", returnType: "int", parameters: [{ name: "nSkill", type: "int" }, { name: "oObject", type: "object", defaultValue: "OBJECT_SELF" }], description: "\n// 870 \n// This function returns the base Skill Rank for the requested \n// skill. It does not include modifiers from effects/items. int GetSkillRankBase(int nSkill, object oObject = OBJECT_SELF);", category: "Character" },
  { name: "EnableRendering", returnType: "void", parameters: [{ name: "oObject", type: "object" }, { name: "bEnable", type: "int" }], description: "\n// 871 \n// This function will allow the caller to modify the rendering behavior \n// of the target object. \n// oObject - The object to change rendering state on. void EnableRendering( object oObject, int bEnable );", category: "General" },
  { name: "GetCombatActionsPending", returnType: "int", parameters: [{ name: "oCreature", type: "object" }], description: "\n// 872 \n// RWT-OEI 10/19/04 \n// This function returns TRUE if the creature has actions in its \n// Combat Action queue. int GetCombatActionsPending(object oCreature);", category: "General" },
  { name: "SaveNPCByObject", returnType: "void", parameters: [{ name: "nNPC", type: "int" }, { name: "oidCharacter", type: "object" }], description: "\n// 873 \n// RWT-OEI 10/26/04 \n// This function saves the party member at that index with the object \n// that is passed in. void SaveNPCByObject( int nNPC, object oidCharacter);", category: "Save/Load" },
  { name: "SavePUPByObject", returnType: "void", parameters: [{ name: "nPUP", type: "int" }, { name: "oidPuppet", type: "object" }], description: "\n// 874 \n// RWT-OEI 10/26/04 \n// This function saves the party puppet at that index with the object \n// that is passed in. For the Remote, just use '0' for nPUP void SavePUPByObject( int nPUP, object oidPuppet );", category: "Save/Load" },
  { name: "GetIsPlayerMadeCharacter", returnType: "int", parameters: [{ name: "oidCharacter", type: "object" }], description: "\n// 875 \n// RWT-OEI 10/29/04 \n// Returns TRUE if the object passed in is the character that the player \n// made at the start of the game int GetIsPlayerMadeCharacter(object oidCharacter);", category: "Object" },
  { name: "RebuildPartyTable", returnType: "void", parameters: [], description: "\n// 876 \n// RWT-OEI 11/12/04 \n// This repopulates the NPCObject table in CSWPartyTable. Do not use this \n// unless you understand exactly what it is doing. void RebuildPartyTable();", category: "General" }
